mapper.select:
select password
from teacher
where teacher_id = #{id};

select student_id, name, sex, phone_number, title, subject.content, subject.subject_id
from student,
     subject
where student.subject_id = subject.subject_id
  and subject.teacher_id = #{id}

mapper.update:
update student
set teacher_id=#{id}
where student_id = #{stuId}

update teacher
set password=#{passwd}
where teacher_id=#{id}
